        public void selectConexionServer()
        {
            try // servidor remoto DESARROLLO .. pendiente a modificaciones
            {
                conexionActual2 = new OdbcConnection(Settings.Default.Seguimiento_ProyectosYPFB_AviacionConnectionStringRemote);
                conexionActual2.Open();
                conexionActual2.Close();
                // la primera conexion se llevo a cabo de manera exitosa
                conexionActual = new OdbcConnectionStringBuilder(ConfigurationManager.ConnectionStrings["TrackingSoft_1._0.Properties.Settings.Seguimiento_ProyectosYPFB_AviacionConnectionStringRemote"].ToString());            
            }
            catch (Exception) // servidor Local HP
            {
                conexionActual2 = new OdbcConnection(Settings.Default.Seguimiento_ProyectosYPFB_AviacionConnectionString);
                conexionActual2.Open();
                conexionActual2.Close();
                // la primera conexion se llevo a cabo de manera exitosa
                conexionActual = new OdbcConnectionStringBuilder(ConfigurationManager.ConnectionStrings["TrackingSoft_1._0.Properties.Settings.Seguimiento_ProyectosYPFB_AviacionConnectionString"].ToString());            
            }
        }
