        private void Inicio_Load(object sender, EventArgs e)
        {
            // cambiar el icono manualmente
            this.Icon = Properties.Resources.yyyy;
            cNotifBol = 0;
            metroStyleManager1.Theme = Global.temaActual;
            ResourceManager x = Resources.ResourceManager;
            Bitmap imagenRecursos;
            string objectImg = metroStyleManager1.Theme == MetroThemeStyle.Dark ? "derRevNegro" : "derRevBlanco";
            imagenRecursos = (Bitmap)x.GetObject(objectImg);
            // cargar el panel de inicio del sistema
            activarPestañas(5);
            cargarIndex();
            pictureBox3.Image = imagenRecursos;
            fijarEtiquetas();
            gvListadoProyectos.ReadOnly = true;
            controlCmb = 0;
            /* implementacion de un hilo que realizara la verificacion constante en la base de datos
             * sobre el estado de los componentes y proyectos para realizar su respectiva conclusion 
