            hilo = new Thread(new ThreadStart(ejecutarVerificacionEstadosComponentesyProyectos));
            hilo.Start();
            //hilo.Suspend();
            //ejecutarVerificacionEstadosComponentesyProyectos();
            
            // realizar la verificacion de los permisos con una consulta
            verificarPermisos(Global.IDUser);
            // opcional... ver si se puede actualizar el estado de las boletas de acuerdo a su cronogram de fechas

            // realizar la verificacion del estado de las boletas con respecto a su cronograma asignado
            verificarAllCronogramaBoletas();
            // ejecutar la consulta para poder ver la cantidad de sesiones abiertas en ese momento
            txtCantSesionGetDate.Text =Gestionar_Usuario.obtenerNroSesionesIniciadasHoy().ToString();
            // verificar la fecha de vencimiento de las boletas para poder notificarlas por email
            notificarBoletasPorVencerPorEmail();

        }

