        private string contenidoHTMLBoletasPorVencer(DataTable tabla)
        {
            string contenido = "";
            for (int i = 0; i < tabla.Rows.Count; i++)
            {
                contenido = contenido + "<TR>";
                for (int j = 0; j < tabla.Columns.Count; j++)
                {
                    contenido = contenido + "<TD>" + tabla.Rows[i][j].ToString() + "</TD>";
                }
                contenido = contenido + "</TR>";
            }
            return contenido;
                            
        }

