                        tbcModControlFases.Visible = false;
                        tbcModAdministracionUsuario.Visible = false;
                        tbcModReportes.Visible = true;
                        pnlInicioSistema.Visible = false;
                        break;
                default:
                        tbcModAdministacionProyectos.Visible = false;
                        tbcModControlFases.Visible = false;
                        tbcModAdministracionUsuario.Visible = false;
                        tbcModReportes.Visible = false;
