        private void btnGuardarProy_Click(object sender, EventArgs e)
        {            
            string codigo = txtCodigoProy.Text;
            string nombre = txtNombreProy.Text;
            string descrip = txtDescripcionProy.Text;
            int tipoProy = int.Parse(cmbTipoProy.SelectedValue.ToString());
            DateTime f1,f2;
            
            f1=DateTime.Parse(dtFechaInicioProy.Text);
            f2=DateTime.Parse(dtFechaFinProy.Text);
            // validar si la fecha es validad para gestionar un proyecto
            // NOTA: solo enviar notificacion sobre que si el proyecto se registra en fecha fuera de rango
            // pero permitir que este se registre
            TimeSpan t = f2 - f1;
            if (t.Days < 153)
            {
                //MessageBox.Show("Elegir un nuevo Cronograma de asignacion para registrar este proyecto");
                MessageBox.Show("El Cronograma del Proyecto Se registra fuera de los valores permitidos \n Por favor Reaujustar las Fechas a futuro....","ASIGNACION DE CRONOGRAMA DE PROYECTO");
                //return;
            }
            crearFecha(f1, f2);
            int idcrono=Actualizar_Estados_Fases.obtenerLastIDCronograma();
            if (int.Parse(cmbTipoProy.SelectedValue.ToString())==1) // tipo de proyecto , Planta Nueva
            {
                string coddpto = cmbCodDptoProy_AgregarProy.SelectedValue.ToString();
                Gestionar_Proyecto.insertarProyecto1(codigo, nombre, descrip, tipoProy, 3, decimal.Parse(txtCostoProy.Text), idcrono,coddpto);
                MessageBox.Show("Proyecto de Construccion de Planta Nueva Registrado Correctamente","REGISTRO DE PROYECTOS");
                /* Preguntar sobre si Asignar Autorizaciones en esta parte al proyecto */
                if (MetroMessageBox.Show(this, "Desea Realizar Asignar Permios y/o Autorizaciones a este Proyecto en este momento??", "Asignacion de Autorizaciones y/o Permisos", MessageBoxButtons.YesNo) == DialogResult.Yes)
                {
                    //MessageBox.Show("Autorizaciones asignadas ahora...");
                    Global.autorizAutomatica = true; 
                    tbcModAdministacionProyectos.SelectedIndex = 4; // pestaña asignar autorizaciones 
                    pnlAsignarAutorizac.Visible = true;
                    pnlDatosProy_AsignarAutoriz.Visible = true;
                    btnGuardar_AsignarAutoriz.Enabled = true;
                    btnCancelar_AsignarAutoriz.Enabled = true;

                    pnlAsignarAutorizac.Enabled = true;
                    pnlAsignarAutoBorrador_AsignarAuto.Enabled = true;
                    pasarParametrosProyecto();
                    recibirParametrosProyectoAutorizaciones();
                    habilitarPanelDatos_AsignarAutorizaciones();
                    btnNuevo_AsignarAutoriz.Enabled = false;
                }
                else
                {
                    MessageBox.Show("Las Autorizaciones Correspondientes se Asignaran Posteriormente","ASIGNACION DE AUTORIZACIONES AL PROYECTO");
                }
                btnCancelarProy_AgregarProyecto.Enabled = true;
                btnNuevoProy_AgregarProy.Enabled = true;
            }
            else // tipo de proyecto : Continuidad Operativa
            {
                /*int codTipoModif=-1;
                if (rbtnModifMayor.Checked){
                   codTipoModif=1;
                } else if (rbtnModfiMenor.Checked){
                   codTipoModif=2;
                } else {
                    MessageBox.Show("Ingrese datos validos");
                    return;
                }
                */
                Gestionar_Proyecto.insertarProyecto2(codigo, nombre, descrip, tipoProy, 3, decimal.Parse(txtCostoProy.Text), idcrono);
                MessageBox.Show("Proyecto de Continuidad Operativa Registrado Correctamente","REGISTRO DE PROYECTOS");
            }
            // Inicializar el estado del proyecto
            Gestionar_Proyecto.iniciarEstado(codigo);
            // cambiar el estado de los controles para las siguientes acciones iterativas
            btnGuardarProy_AgregarProyecto.Enabled = false;
            btnCancelarProy_AgregarProyecto.Enabled = false;
            btnNuevoProy_AgregarProy.Enabled = true;
        }

