            //IDAutorizacionesAsig = new int[30];
            IDAutorizacionesAsig = new List<int>();
        }

        
        private void btnAsignarAutorizaRespon_Click(object sender, EventArgs e)
        {
            Boolean sw=false;
            // para proyectos
            string codProy = txtCodProy_AsignarAutor.Text;
            // para compnonentes
            // para ambos los datos siguientes
            string codautori = cmbAutorizacion_AsignarAutoriz.SelectedValue.ToString();
            string aliaspers = cmbResponsable_AsignarAutoriz.SelectedValue.ToString();
            DateTime fechaMaxFin = DateTime.Parse(dtFechaMaxFin_AsignarAutroriz.Text);
            Cronograma cr = Gestionar_Proyecto.obtenerCronogramaProyecto(codProy);
                
            if (rbtnProyectos_AsginarAutoriz.Checked) // asignacion de autorizaciones manuales a proyecto de planta nueva
            {
                sw = true;
                if (fechaMaxFin >= cr.Fecha_Inicio && fechaMaxFin <= cr.Fecha_Conclusion)
                {
                    Gestionar_Autorizacion.insertarAutorizacionProyecto(codProy, codautori, aliaspers, fechaMaxFin);
                    gvListadoAutorizResp.DataSource = Gestionar_Autorizacion.mostrarAutorizacionesProyecto(codProy);
                    Global.notifAsig = "Proyecto";
                }
                else
                {
                    MessageBox.Show("No se Puede Asignar esta fecha por que no es valida con el Cronograma del Proyecto");
                    return;
                }
            }
            else if (rbtnComponentes_AsignarAutoriz.Checked) // asignacion de autorizaciones manuales a componentes de continuidad
            {
                sw = true;
                string codcomp = cmbCodCompo_DatosCompoAsignAutoriz.SelectedValue.ToString();
                cr = Gestionar_Componente.obtenerCronogramaComponente(codcomp);
                //Cronograma cr = Gestionar_Componente.obtenerCronogramaComponente(codcomp);
                if (fechaMaxFin >= cr.Fecha_Inicio && fechaMaxFin <= cr.Fecha_Conclusion)
                {
                    Gestionar_Autorizacion.insertarAutorizacionComponente(codcomp, codautori, aliaspers, fechaMaxFin);
                    gvListadoAutorizResp.DataSource = Gestionar_Autorizacion.mostrarAutorizacionesComponente(codcomp);
                    Global.notifAsig = "Componente";
                }
                else
                {
                    MessageBox.Show("No se Puede Asignar esta fecha por que no es valida con el Cronograma del Componente");
                    return;
                }
            }
            else if (pnlDatosCompX_AsignarAutoriz.Visible) // asignacion de autorizaciones automaticas a componentes de continuidad operativa
            {
                sw = true;
                string codigoComp = txtCodComp_DatosCompX_AsignarAutoriz.Text;
                cr = Gestionar_Componente.obtenerCronogramaComponente(codigoComp);
                
                if (fechaMaxFin >= cr.Fecha_Inicio && fechaMaxFin <= cr.Fecha_Conclusion)
                {
                    Gestionar_Autorizacion.insertarAutorizacionComponente(codigoComp, codautori, aliaspers, fechaMaxFin);
                    gvListadoAutorizResp.DataSource = Gestionar_Autorizacion.mostrarAutorizacionesComponente(codigoComp);
                    Global.notifAsig = "Componente";
                }
                else
                {
                    MessageBox.Show("No se Puede Asignar esta fecha por que no es valida con el Cronograma del Componente");
                    return;
                }
            }
            else if (pnlDatosProy_AsignarAutoriz.Visible) // asignacion de autorizaciones manuales a los proyectos de planta nueva
            {
                sw = true;
                //string codigoProy = txtCodProy_AsignarAutor.Text;
                if (fechaMaxFin >= cr.Fecha_Inicio && fechaMaxFin <= cr.Fecha_Conclusion)
