        private void ObtenerDatosProyecto(string codigo)
        {
            List<string> l1 = new List<string>();
            DataTable dt = Gestionar_Proyecto.obtenerDatosProyecto(codigo);
            if (dt.Rows.Count != 0)  // hay registros en la base de datos
            {
                foreach (DataRow df in dt.Rows)
                {
                    foreach (DataColumn dc in dt.Columns)
                    {
                        l1.Add(df[dc].ToString());
                    }
                }
                if (l1[1].ToString().Trim().Equals("Construccion Planta Nueva")){
                    txtNombreProy_AsignarAutoriz.Text = l1[0];
                    txtTipoProy_AsignarAutoriz.Text = l1[1];
                    btnAsignarAutorizaRespon.Enabled = true;
                } 
                else
                {
                    btnAsignarAutorizaRespon.Enabled = false;
                    MessageBox.Show("Proyecto no encontrado o no pertenece a Proyectos de Planta Nueva","BUSQUEDA DE PROYECTOS");
                }
            }
            else 
            {
                MessageBox.Show("Codigo de Proyecto incorrecto, intente de nuevo por favor");
                btnAsignarAutorizaRespon.Enabled = false;
            }
        }
       
