        private void enviarNotificacionesUserAutorizacionesAsig(string tipoNotif)
        {
            int cantTuplasAsig=gvListadoAutorizResp.Rows.Count;
            int[] notifID=new int[cantTuplasAsig];
            string[] notifUser = new string[cantTuplasAsig];
            int i=0;
            // ad vector de si la autorizacion fue notificada
            string[] estadoNotifAsig = new string[cantTuplasAsig];
            foreach (DataGridViewRow row in gvListadoAutorizResp.Rows)
            {                
                notifID[i] =int.Parse(row.Cells[0].Value.ToString());
                estadoNotifAsig[i] = Gestionar_Autorizacion.obtenerEstadoNotifAsignacionAutorizacion(notifID[i],tipoNotif);
                string aliasPersSelect = Gestionar_Autorizacion.obtenerAliasPersonaRespAutoriz(notifID[i],tipoNotif);
                //string[] responsable=row.Cells[3].Value.ToString().Split(' ');
                // cambiar el obtener id mandar de parametro el alias de la persona para obtener el id
                
                //notifUser[i] = Gestionar_Usuario.obtenerID(responsable[0], responsable[1]);
                notifUser[i] = Gestionar_Usuario.obtenerID(aliasPersSelect);
                i++;
            }
            // crear la lista de datos que se requiere para el detalle del envio de correos 
            List<Object> datosAsignacionAutoriz;
            for (int j = 0; j < cantTuplasAsig; j++)
            {
                if (estadoNotifAsig[j].Trim().Equals("NO"))
                {
                    Administrar_Tareas_Notificaciones.generarAlertaAutorizaciones(Global.notifAsig, notifID[j], notifUser[j]);
                    // Notificar mediante correo electronico a sobre la asignacion reciente
                    string emailPersonal = Gestionar_Usuario.obtenerEmail(notifUser[j]);
                    string asunto = "ALERTA DE NUEVA AUTORIZACION ASIGNADA";                    
                    // ver el mensaje que se enviara
                    string mensaje = "";
                    EnvioEmail servidorCorreo = new EnvioEmail();
                    if (Global.notifAsig.Equals("Proyecto"))
                    {
                        datosAsignacionAutoriz=Gestionar_Autorizacion.obtenerDatosAsignacionAutorizacionProyecto(notifID[j]); 
                        //string nombreProyecto=Gestionar_Autorizacion.obtenerNombreProyecto(notifID[j]);
                        //string autorizacion=Gestionar_Autorizacion.obtenerNombreAutorizacion
                        mensaje = "USTED A RECIBIDO UNA NUEVA AUTORIZACION O PERMISO DE PROYECTO CON EL SIGUIENTE DETALLE:</br></br></br>" +
                                  "<b>PROYECTO:</b>        " + datosAsignacionAutoriz[0].ToString() + "</br></br>" +
                                  "<b>AUTORIZACION:</b>    " + datosAsignacionAutoriz[1].ToString() + "</br></br>" +
                                  "<b>FECHA PLAZO:</b>     " + datosAsignacionAutoriz[2].ToString() + "</br></br></br>" +
                                  "<b>NOTA:</b> VERIFICAR LA INFORMACION DEL PRESENTE CORREO JUNTO CON LA DEL SISTEMA, GRACIAS....";
                    }
                    else
                    {
                        datosAsignacionAutoriz = Gestionar_Autorizacion.obtenerDatosAsignacionAutorizacionComponente(notifID[j]);
                        mensaje = "USTED A RECIBIDO UNA NUEVA AUTORIZACION O PERMISO DE COMPONENTE CON EL SIGUIENTE DETALLE:</br></br></br>" +
                                  "<b>PROYECTO:</b>        " + datosAsignacionAutoriz[0].ToString() + "</br></br>" +
                                  "<b>COMPONENTE:</b>      " + datosAsignacionAutoriz[1].ToString() + "</br></br>" +
                                  "<b>AUTORIZACION:</b>    " + datosAsignacionAutoriz[2].ToString() + "</br</br>" +
                                  "<b>FECHA PLAZO:</b>     " + datosAsignacionAutoriz[3].ToString() + "</br></br></br>" +
                                  "<b>NOTA:</b> VERIFICAR LA INFORMACION DEL PRESENTE CORREO JUNTO CON LA DEL SISTEMA, GRACIAS....";
                    }
                    servidorCorreo.añadirDestinatario(emailPersonal);
                    servidorCorreo.enviarCorreo(asunto, mensaje);
                    datosAsignacionAutoriz = null;
                    // actualizar el estado de las asignacion de autorizacion [Notificado]
                    Gestionar_Autorizacion.actualizarSINotificado(Global.notifAsig,notifID[j]);
                }                
            }            
        }

