        private void btnGuardar_AgregarComp_Click(object sender, EventArgs e)
        {
            string codProy = cmbCodProy_AgregarComp.SelectedValue.ToString()/*.Trim()*/;
            string codComp = txtCodComp_AgregarComp.Text;
            string nombreComp = txtNombreComp_AgregarComp.Text;
            string descComp = txtDescrip_AgregarComp.Text;
            DateTime fechaInicio = DateTime.Parse(dtFechaInicio_AgregarComp.Text);
            DateTime fechaFin = DateTime.Parse(dtFechaFin_AgregarComp.Text);

            Cronograma crProyecto=Gestionar_Proyecto.obtenerCronogramaProyecto(codProy);
            
            DateTime fechaInicioProyecto = crProyecto.Fecha_Inicio;
            DateTime fechaFinProyecto = crProyecto.Fecha_Conclusion;
            TimeSpan t=fechaFin-fechaInicio;

            if (fechaInicioProyecto> fechaInicio || fechaFinProyecto < fechaFin/*|| t.Days<190*/) 
            {
                //MessageBox.Show("Cronograma No Valido, no esta dentro del rango del cronograma del proyecto");
                MessageBox.Show("Cronograma fuera de rango, el Componente sera registrado pero revisar las fechas posteriormente");
                //return;
            }
            
            // crear el id de esta fecha                
            Actualizar_Estados_Fases.insertarCronograma(fechaInicio, fechaFin);
            int idCrono = Actualizar_Estados_Fases.obtenerLastIDCronograma();
            int tipoModif;
            if (pnlTipoModifComp_AgregarComp.Visible) // si selecciono proyecto de continuidad operativa
            {
                if (rbtnTipoModifMenor_AgregarComp.Checked) // menor modificacion seleccionada
                {
                    tipoModif = 2;
                }
                else if (rbtnTipoModifMayor_AgregarComp.Checked)
                {
                    tipoModif = 1;
                }
                else
                {
                    MessageBox.Show("Seleccione algun tipo de modificacion", "SELCCION DE TIPO DE MODIFICACION");
                    return;
                }
                Gestionar_Componente.insertarComponente(codComp, nombreComp, descComp, codProy, idCrono, tipoModif);
                MessageBox.Show("Componente Registrado Correctamente", "Gestion de Componentes de Proyecto");
                btnGuardar_AgregarComp.Enabled = false;
                btnCancelar_AgregarComp.Enabled = false;
                btnNuevo_AgregarComp.Enabled = true;
                // Preguntar si desea asignar las autorizaciones correspondientes a este componente
                if (tipoModif == 1)
                {
                    if (MetroMessageBox.Show(this, "Desea asignar las correpondientes Autorizaciones al Componente Registrado???", "REGISTRO DE COMPONENTES", MessageBoxButtons.YesNo) == DialogResult.Yes)
                    {
                        Global.autorizAutomatica=false;
                        tbcModAdministacionProyectos.SelectedIndex = 4;
                        pnlAsignarAutorizac.Visible = true;
                        habilitarPanelDatos_AsignarAutorizaciones();
                        btnNuevo_AsignarAutoriz.Enabled = false;
                        btnAsignarAutorizaRespon.Enabled = true;
                        cargarCmbResponsable();
                        cargarCmbAutorizacionTipoProy_CompTipoModif("Continuidad Operativa", false);
                        pasarDatosComponete_a_AsignarAutorizaciones();
                    }
                }
                Gestionar_Componente.iniciarEstadoComponente(codComp);
                return;
            }
            else // se selecciono proyecto de planta nueva  ...int.MaxValue es como para darle un valor null
            {
                Gestionar_Componente.insertarComponente(codComp, nombreComp, descComp, codProy, idCrono,int.MaxValue);                
            }
            MessageBox.Show("Componente Registrado Correctamente", "Gestion de Componentes de Proyecto");
            Gestionar_Componente.iniciarEstadoComponente(codComp);
            btnGuardar_AgregarComp.Enabled = false;
            btnCancelar_AgregarComp.Enabled = false;
