            TimeSpan t = f2 - f1;
            /*if (t.Days>=190)
            {
                /*Cronograma cronoCompUpdate = new Cronograma();
                cronoCompUpdate.Id = Gestionar_Componente.obtenerIDCronogramaComp_o_Subcomp(codcomp);
