                /*Actualizar_Estados_Fases.insertarCronograma(f1, f2);
                Gestionar_Componente.actualizarComponente(codcomp, nombrecomp, desccomp, Actualizar_Estados_Fases.obtenerLastIDCronograma());
                MessageBox.Show("Componente Actualizado Satisfactoriamente", "ACTUALIZACION DE COMPONENTES");
