            /*}
            else
            {
                MessageBox.Show("Seleccione una Fecha de Finalizacion valida para el Componente", "ACTUALIZACION DE COMPONENTES");
            }*/
            
            Cronograma cr = Gestionar_Proyecto.obtenerCronogramaProyecto_desdeComponente(codcomp);
            if (cr.Fecha_Conclusion < f2)
            {
                //MessageBox.Show("Seleccione una fecha de finalizacion valida para la actualizacion", "ACTUALIZACION DE COMPONENTES");
                //return;
                MessageBox.Show("fecha de finalizacion fuera de rango para la actualizacion del componente, se guardaran los datos de todas formas pero verificar fechas a futuro,", "ACTUALIZACION DE COMPONENTES");
            }
