            {
                MessageBox.Show("Los datos del componente se actualizaran,\n pero el cronograma esta fuera del rango permitido, porfavor verificar las fechas a futuro.... ", "ACTUALIZACION DE COMPONENTES");      
            }*/
            Actualizar_Estados_Fases.insertarCronograma(f1, f2);
            int x=Actualizar_Estados_Fases.obtenerLastIDCronograma();
            if (cmbTipoModifComp_ActualizarComponente.Enabled == true) // ver si el comboBox esta habilitado si actualizara el tipo de modificacion
            {
                int idtipoModifSelect = int.Parse(cmbTipoModifComp_ActualizarComponente.SelectedValue.ToString());
                Gestionar_Componente.actualizarComponente(codcomp, nombrecomp, desccomp,x ,idtipoModifSelect);
                MessageBox.Show("Componente Actualizado Satisfactoriamente", "ACTUALIZACION DE COMPONENTES");
