                    habilitarPanelDeAutorizacionesParaComponentes();
                }
                else
                {
                    // eliminar las autorizaciones asignadas previamentr
                    if (idtipoModifSelect.Equals(2) && Global.tipoModifComponente.Equals("Modificacion Mayor")) 
