        private void pasarParametrosSubcomponentes(int fila)
        {
            string[] datosSubComponente = new string[gvSubComponentes_ListadoSubComp.ColumnCount];
            for (int i = 0; i < datosSubComponente.Length; i++)
            {
                datosSubComponente[i] = gvSubComponentes_ListadoSubComp.Rows[fila].Cells[i].Value.ToString();
            }
            if (rbtnPlantaNueva_TipoProy_ListadoSubComp.Checked) // planta nueva esta check
            {
                txtTipoProy_ActualizarSubcomp.Text = rbtnPlantaNueva_TipoProy_ListadoSubComp.Text;
            }
            else // continuidad operativa esta check
            {
                txtTipoProy_ActualizarSubcomp.Text = rbtnContinuidad_TipoProy_ListadoSubcomp.Text; 
            }
            txtNombreProy_ActualizarSubcomp.Text=txtNombreProy_ListadoSubComp.Text;
            txtNombreComp_ActualizarSubcomp.Text = cmbNombreComp_ListadoSubComp.GetItemText(cmbNombreComp_ListadoSubComp.SelectedItem);
            txtCodSubComp_ActualizarSubcomp.Text = datosSubComponente[0];
            txtNombreSubcomp_ActualizarSubcomp.Text = datosSubComponente[1];
            //txtDescSubcomp_ActualizarSubcomp.Text ="Ingrese nueva Descripcion..." /*datosSubComponente[2]*/;
            txtDescSubcomp_ActualizarSubcomp.Text = Gestionar_Componente.obtenerDescComponente(datosSubComponente[0].ToString());
            txtFechaInicioSubcomp_ActualizarSubcomp.Text = datosSubComponente[2];
            txtFechaConclusionSubcomp_ActualizarSubcomp.Text = datosSubComponente[3];
            dtFechaFinSubcomp_ActualizarSubcomp.Value = DateTime.Parse(txtFechaConclusionSubcomp_ActualizarSubcomp.Text);
        }

