        private void gvFasesAsignadas_ActualizarCronogramaFases_CellDoubleClick(object sender, DataGridViewCellEventArgs e)
        {
            if (MetroMessageBox.Show(this, "Seleccionar la Fase Actual como la Fase en Ejecucion del Subcomponente", "Seleccion de Fases", MessageBoxButtons.YesNo) == DialogResult.Yes)
            {
                //realizar el cierre de estado o estado=concluido/finalizado de las anteriores fases   
                string codsubcomp = cmbNombreSubComp_ActualizarFasesEstado.SelectedValue.ToString();
                string idfase = gvFasesAsignadas_ActualizarFasesEstado.Rows[e.RowIndex].Cells[0].Value.ToString();
                int nroPasoFase = int.Parse(gvFasesAsignadas_ActualizarFasesEstado.Rows[e.RowIndex].Cells[1].Value.ToString());
                Asignar_Fases_Responsables.actualizarEstadoActual(codsubcomp,nroPasoFase);
                // realizar la notificion para el usuario
                int idetapa=Administrar_Tareas_Notificaciones.obtenerIDEtapa(idfase,codsubcomp);
                // obtener el usuario al que se le asigno la etapa
                string nombreResponsable=gvFasesAsignadas_ActualizarFasesEstado.Rows[e.RowIndex].Cells[3].Value.ToString();
                // obtener el alias de la persona responsable
                string aliasPersonaSelect = Administrar_Tareas_Notificaciones.obtenerAliasPersonaSeleccionadaFase(idetapa);
                // separar nombres y apellidos
                String[] datosResponsable=nombreResponsable.Split(' ');
                //string iduserSelect = Gestionar_Usuario.obtenerID(datosResponsable[0], datosResponsable[1]);
                string iduserSelect = Gestionar_Usuario.obtenerID(aliasPersonaSelect);
                Administrar_Tareas_Notificaciones.generarAlertaEtapaUser(/*Global.IDUser*/iduserSelect, idetapa);
                // enviar el correo de notificacion sobre la fase actual y a quien le pertenece
                string nombreComp = Gestionar_Componente.obtenerNombreComponenteFromSubcomp(codsubcomp);
                
                string emailPersonal = Gestionar_Usuario.obtenerEmail(iduserSelect);
                List<Object> datosParaEmail = Administrar_Tareas_Notificaciones.obtenerDatosGeneralesEmail(iduserSelect,idfase,codsubcomp);

                string asunto = "ALERTA DE NUEVO PROCESO PENDIENTE DE SU REALIZACION";
                string mensaje = "USTED FUE ASIGNADO PARA LA REALIZACION DE UNA NUEVA FASE DEL PROCESO CON EL SIGUIENTE DETALLE:</br></br></br>" +
                                 "<b>PROYECTO:</b>              " + datosParaEmail[0].ToString() + "</br></br>" +
                                  "<b>COMPONENTE:</b>           " + nombreComp + "</br></br>" +
                                  "<b>SUBCOMP. O PROCESO:</b>   " + datosParaEmail[1].ToString() + "</br></br>" +
                                  "<b>FASE:</b>                 " + datosParaEmail[2].ToString() + "</br></br>" +
                                  "<b>FECHA INICIO:</b>         " + datosParaEmail[3].ToString() + "</br></br>" +
                                  "<b>FECHA FINALIZACION:</b>   " + datosParaEmail[4].ToString() + "</br></br></br>" +
                                  "<b>NOTA:</b> VERIFICAR LA INFORMACION DEL PRESENTE CORREO JUNTO CON LA DEL SISTEMA, GRACIAS....";
                EnvioEmail servidorCorreo = new EnvioEmail();
                servidorCorreo.añadirDestinatario(emailPersonal);
                servidorCorreo.enviarCorreo(asunto, mensaje);
                
                // Actualizar al estado de ejecucion del subcomponente, componente y proyecto tambien
                Gestionar_Componente.updateEstadoSubcomponente(cmbNombreSubComp_ActualizarFasesEstado.SelectedValue.ToString());
                Gestionar_Componente.updateEstadoComponente(cmbNombreComp_ActualizarFasesEstado.SelectedValue.ToString());
                Gestionar_Proyecto.updateEstadoProyecto(cmbCodProy_ActualizarFasesEstado.SelectedValue.ToString());
                
                MessageBox.Show("Fase de Ejecucion del Subcomponente Actualizada Correctamente","ACTUALIZACION DE FASE DE EJEUCUCION");
                // enviar notificacion que el usuario vea en su lista de tareas
                gvFasesAsignadas_ActualizarFasesEstado.DataSource = null;
            }
        }

