        private void llenarListaIDEstadoFaseComponente()
        {
            IDEstadoFaseComponente = new List<int>();
            int lastColumn=gvFaseActualProySubcomponentes.Columns.Count-1;
            for (int i = 0; i < gvFaseActualProySubcomponentes.Rows.Count;i++)
            {
                IDEstadoFaseComponente.Add(int.Parse(gvFaseActualProySubcomponentes[lastColumn, i].Value.ToString()));     
            }
        }

