        /* Metodo que modificara los valores del Objeto -> tableLogInfo
         * en los atributos [attributes] y [logonProperties] para alli establecer
         * un servidor y base de datos dinamicos de acuerdo al ordenador en 
         * el que se encuentre y se ejecute el sistema
         */
        public TableLogOnInfo getSQLTableLogOnInfo(ref TableLogOnInfo tableLogInfo,string server,string dbname)
        {
            tableLogInfo.ConnectionInfo.ServerName = server;
            tableLogInfo.ConnectionInfo.DatabaseName = dbname;
            tableLogInfo.ConnectionInfo.Type = ConnectionInfoType.SQL;

            tableLogInfo.ConnectionInfo.Attributes.Collection.Set("Database DLL", "crdb_ado.dll");
            tableLogInfo.ConnectionInfo.Attributes.Collection.Set("QE_DatabaseName", dbname);
            tableLogInfo.ConnectionInfo.Attributes.Collection.Set("QE_DatabaseType", "OLE DB (ADO)");
            // seccion donde se setearan los atributos de la logonProperties
            DbConnectionAttributes logonAtrib = new DbConnectionAttributes();
            logonAtrib.Collection.Add(new NameValuePair2("Application Intent", "READWRITE"));
            logonAtrib.Collection.Add(new NameValuePair2("Auto Translate", "-1"));
            logonAtrib.Collection.Add(new NameValuePair2("Connect Timeout", "15"));
            logonAtrib.Collection.Add(new NameValuePair2("Data Source", server));
            logonAtrib.Collection.Add(new NameValuePair2("DataTypeCompatibility", "0"));
            logonAtrib.Collection.Add(new NameValuePair2("General Timeout", "0"));
            logonAtrib.Collection.Add(new NameValuePair2("Initial Catalog", dbname));
            logonAtrib.Collection.Add(new NameValuePair2("Integrated Security", "True"));
            logonAtrib.Collection.Add(new NameValuePair2("Locale Identifier", "3082"));
            logonAtrib.Collection.Add(new NameValuePair2("MARS Connection", "0"));
            logonAtrib.Collection.Add(new NameValuePair2("OLE DB Services", "-5"));
            logonAtrib.Collection.Add(new NameValuePair2("Provider", "SQLNCLI11"));
            logonAtrib.Collection.Add(new NameValuePair2("Tag with column collation when possible", "0"));
            logonAtrib.Collection.Add(new NameValuePair2("Trust Server Certificate", "0"));
            logonAtrib.Collection.Add(new NameValuePair2("Use DSN Default Properties", "False"));
            logonAtrib.Collection.Add(new NameValuePair2("Use Encryption for Data", "0"));
            // asignar estos atributos creados

            tableLogInfo.ConnectionInfo.Attributes.Collection.Set("QE_LogonProperties",logonAtrib);
            tableLogInfo.ConnectionInfo.Attributes.Collection.Set("QE_ServerDescription", server);
            tableLogInfo.ConnectionInfo.Attributes.Collection.Set("QE_SQLDB", "True");
            tableLogInfo.ConnectionInfo.Attributes.Collection.Set("SSO Enabled", "False");

            return tableLogInfo;

        }
        private string mostrarPruebaConexion(Array f,TableLogOnInfo xd)
        {
            string x = ""; int a=0; int aa=0;
            foreach (NameValuePair2 nameValueAct in f)
            {
                a++;
                x = x + a.ToString()+". " +nameValueAct.Name + ":    " + nameValueAct.Value + "\n";
                if (a.Equals(4))
                {
                    NameValuePairs2 gh =xd.ConnectionInfo.LogonProperties;
                    Array c = gh.ToArray(new NameValuePair2().GetType());
                    
                    foreach (NameValuePair2 nameActXD in c)
                    {
                        aa++;
                        x = x + "       "+a.ToString()+"." +aa.ToString()+". -> " + nameActXD.Name + ":   " + nameActXD.Value + "\n";  
                    }
                }
            }
            
            return x;
        }

