        private void generarReporte_EstadoAutorizacionesProyectoComponente(int indicador,string codproyecto)
        {
            ReportDocument reporte = new ReportDocument();
            string direccion = Path.Combine(rutaRun, @"rptEstado_AutorizacionesAsignadas.rpt");
            reporte.Load(direccion);
            //OdbcDataAdapter adapter = new OdbcDataAdapter("reporteEstadoAutorizacionesAsignadas", conexionActual2);
            OdbcDataAdapter adapter = new OdbcDataAdapter(new OdbcCommand("{?= call reporteEstadoAutorizacionesAsignadas (?,?)}", conexionActual2));
            adapter.SelectCommand.CommandType=CommandType.StoredProcedure;
            OdbcParameter param = new OdbcParameter("@RETURN_VALUE", OdbcType.Int);
            param.Direction = ParameterDirection.ReturnValue;
            adapter.SelectCommand.Parameters.Add(param);
            adapter.SelectCommand.Parameters.Add("@ind",OdbcType.Int);
            adapter.SelectCommand.Parameters.Add("@codproyecto", OdbcType.VarChar, 10);
            adapter.SelectCommand.Parameters[1].Value=indicador;
            adapter.SelectCommand.Parameters[2].Value = codproyecto;
            Seguimiento_ProyectosYPFB_AviacionDataSet datataset = new Seguimiento_ProyectosYPFB_AviacionDataSet();
            datataset.EnforceConstraints = false;
            // setear valores de los textos del reporte
            
            if (codproyecto.Length > 0)
            {
                ((TextObject)reporte.ReportDefinition.ReportObjects["lblRptProyecto_EstadoAutorizaciones"]).Text = Gestionar_Proyecto.obtenerDatosProyecto(codproyecto).Rows[0][0].ToString();
            }
            else
            {
                ((TextObject)reporte.ReportDefinition.ReportObjects["lblRptProyecto_EstadoAutorizaciones"]).Text = "Todos";
            }
            if (rbtnPlantaNueva_rptEstadoAutorizaciones.Checked)
            {
                ((TextObject)reporte.ReportDefinition.ReportObjects["lblRptTipoProyecto_EstadoAutorizaciones"]).Text = "Construccion Planta Nueva";
            }
            else
            {
                ((TextObject)reporte.ReportDefinition.ReportObjects["lblRptTipoProyecto_EstadoAutorizaciones"]).Text = "Continuidad Operativa";
            }   
            adapter.Fill(datataset, "reporteEstadoAutorizacionesAsignadas");
            reporte.SetDataSource(datataset);
            rptReporte3.ReportSource=reporte;
            reporte.Refresh();
            
        }

