                    int idalerta = int.Parse(gvListadoNotificaciones_AdministrarAlertasNotif[0, e.RowIndex].Value.ToString());
                    Global.idAlerta = idalerta;
                    // instanciar una lista para poder guardar los datos que se obtengan
                    List<Object> datosGet=new List<Object>();
                    switch (gvListadoNotificaciones_AdministrarAlertasNotif[2,e.RowIndex].Value.ToString())
                    {
                        case "Autorizaciones Proyectos": // ir a la tabla [Proyecto_Autorizacion]
                            int idProyAutoriz=Administrar_Tareas_Notificaciones.obtenerIDProyAutorizacion(idalerta);
                            pnlTareaAutoriz_EjecutarTarea_AdmTareasFases.Visible=true;
