                            Autorizacion_EjecucionTarea_PnlAsignacionAutoriz.Text = datosGet[1].ToString();
                            FechaPlazo_EjecucionTarea_PnlAsignacionAutoriz.Text = DateTime.Parse(datosGet[2].ToString()).Date.ToString("dd/MM/yyyy");
                            EstadoActual_EjecucionTarea_PnlAsignacionAutoriz.Text = datosGet[3].ToString().ToUpper();
                            // ver el panel de botones para mostrar
                            pnlBtnAceptarTarea_EjecucionTarea_AdmTareaFases.Visible = false;
                            pnlBtnEjecutarTarea_EjecutarTarea_AdmTareaFases.Visible = true;
                            break;
                        case "Autorizaciones Componentes": // ir a la tabla [Componente_Autorizacion]
                            int idComponAutoriz=Administrar_Tareas_Notificaciones.obtenerIDCompAutorizacion(idalerta);
                            pnlTareaAutoriz_EjecutarTarea_AdmTareasFases.Visible=true;
                            pnlTareaEtapa_EjecutarTarea_AdmTareaFase.Visible=false;                           
                            txtNombreTarea_EjecucionTarea_AdmTareaFase.Text = "ASIGNACION DE AUTORIZACIONES";
                            txtTipoTarea_EjecucionTarea_AdmTareaFase.Text = "AUTORIZACION DE COMPONENTE";
                            datosGet=Administrar_Tareas_Notificaciones.obtenerDatos("Componente_Autorizacion",idComponAutoriz);
                            txtAsignadoA_EjecucionTarea_PnlAsignacionAutoriz.Text = datosGet[0].ToString();
                            Autorizacion_EjecucionTarea_PnlAsignacionAutoriz.Text = datosGet[1].ToString();
                            FechaPlazo_EjecucionTarea_PnlAsignacionAutoriz.Text = DateTime.Parse(datosGet[2].ToString()).Date.ToString("dd/MM/yyyy");                            
                            EstadoActual_EjecucionTarea_PnlAsignacionAutoriz.Text = datosGet[3].ToString().ToUpper();
                            
                            pnlBtnAceptarTarea_EjecucionTarea_AdmTareaFases.Visible = false;
                            pnlBtnEjecutarTarea_EjecutarTarea_AdmTareaFases.Visible = true;
                            
                            break;                         
                        default:  // caso de haber seleccionado las etapas; ir a la tabla [Fase_Componente]
                            int idFaseComp=Administrar_Tareas_Notificaciones.obtenerIDFaseComponente(idalerta);
                            pnlTareaEtapa_EjecutarTarea_AdmTareaFase.Visible=true;
                            pnlTareaAutoriz_EjecutarTarea_AdmTareasFases.Visible=false;    
                            txtNombreTarea_EjecucionTarea_AdmTareaFase.Text = "ASIGNACION DE FASE DE SUBCOMPONENTE";
                            txtTipoTarea_EjecucionTarea_AdmTareaFase.Text = "FASE O ETAPA";
                            datosGet=Administrar_Tareas_Notificaciones.obtenerDatos("Fase_Componente",idFaseComp);
                            Subcomponente_EjecutarTarea_PnlEtapa.Text = datosGet[0].ToString();
                            FaseAsig_EjecutarTarea_PnlEtapa.Text = datosGet[1].ToString();
                            FechaInicio_EjecutarTarea_PnlEtapa.Text = DateTime.Parse(datosGet[2].ToString()).Date.ToString("dd/MM/yyyy");
                            FechaFin_EjecutarTarea_PnlEtapa.Text = DateTime.Parse(datosGet[3].ToString()).Date.ToString("dd/MM/yyyy");
                            EstadoActual_EjecutarTarea_PnlEtapa.Text = datosGet[4].ToString().Trim().ToUpper();
                            bool sw;
                            if (!EstadoActual_EjecutarTarea_PnlEtapa.Text.Equals("PENDIENTE"))
                            {
                                sw = true;
                            }
                            else
                            {
                                sw = false;
                            }
                            pnlBtnAceptarTarea_EjecucionTarea_AdmTareaFases.Visible = !sw;
                            pnlBtnEjecutarTarea_EjecutarTarea_AdmTareaFases.Visible = sw;
                            
                            break;
                    }
                }
            }
            else
            {
                MetroMessageBox.Show(this, "\nUsted Ya Reviso la Notificacion Seleccionada\nPorfavor ingresar directamente desde la pestaña ADMINISTRAR TAREAS O FASES", "ALERTAS Y NOTIFICACIONES");
            }
        }

        private void gvListadoTareas_AdmTareasFases_CellDoubleClick(object sender, DataGridViewCellEventArgs e)
        {
            string estadoSelect=gvListadoTareas_AdmTareasFases[gvListadoTareas_AdmTareasFases.ColumnCount-1,e.RowIndex].Value.ToString();
            if (!estadoSelect.Equals("Finalizada") && !estadoSelect.Equals("En Mora"))
            {
                if (MetroMessageBox.Show(this, "Desea Actualizar el Estado Actual de la Tarea Seleccionada??", "ADMINISTRACION DE TAREAS", MessageBoxButtons.YesNo) == DialogResult.Yes)
                {
                    pnlListadoTareas_AdministrarTareasFases.Enabled = false;
