                    if (rbtnFasesEtapas_SelectTipo.Checked) // seleccciono etapa
                    {
                        pnlTareaAutoriz_EjecutarTarea_AdmTareasFases.Visible = false;
                        pnlTareaEtapa_EjecutarTarea_AdmTareaFase.Visible = true;
