                        FechaInicio_EjecutarTarea_PnlEtapa.Text =DateTime.Parse(gvListadoTareas_AdmTareasFases[2, e.RowIndex].Value.ToString()).Date.ToString("dd/MM/yyyy");
                        FechaFin_EjecutarTarea_PnlEtapa.Text =DateTime.Parse(gvListadoTareas_AdmTareasFases[3, e.RowIndex].Value.ToString()).Date.ToString("dd/MM/yyyy");
                        EstadoActual_EjecutarTarea_PnlEtapa.Text = gvListadoTareas_AdmTareasFases[4, e.RowIndex].Value.ToString().ToUpper();
                        // verificar el estado de la tarea para dar las opciones  a elegir al usuario
                        if (!EstadoActual_EjecutarTarea_PnlEtapa.Text.Equals("PENDIENTE"))
                        {
                            pnlBtnEjecutarTarea_EjecutarTarea_AdmTareaFases.Visible = true;
                            pnlBtnAceptarTarea_EjecucionTarea_AdmTareaFases.Visible = false;
                        }
                        else
                        {
                            pnlBtnEjecutarTarea_EjecutarTarea_AdmTareaFases.Visible = false;
                            pnlBtnAceptarTarea_EjecucionTarea_AdmTareaFases.Visible = true;
                        }
                        // obtener el id de la notificacion a eliminar 
                        string codSubcomp=Gestionar_Componente.obtenerIDComponente(Subcomponente_EjecutarTarea_PnlEtapa.Text);
                        string idfase=Administrar_Tareas_Notificaciones.obtenerIDFase(FaseAsig_EjecutarTarea_PnlEtapa.Text);
