        // opcion para finalizar la tarea seleccionada 
        private void btnFinalizarTarea_EjecutarTarea_Click(object sender, EventArgs e)
        {
            int tipo;
            if (txtNombreTarea_EjecucionTarea_AdmTareaFase.Text.Equals("ASIGNACION DE FASE DE SUBCOMPONENTE"))
            {
                tipo = 1;
            }
            else // ASIGNACION DE AUTORIZACIONES
            {
                tipo = 2;
            }
            if (MetroMessageBox.Show(this, "Esta Seguro que Desea Finalizar en este Momento la Tarea Pendiente???", "ADMINISTRACION DE TAREAS", MessageBoxButtons.YesNo) == DialogResult.Yes)
            {
                if (tipo.Equals(1)) // tareas de tipo ASIGANCION DE ETAPAS
                {
                    string idSubcomponente = Gestionar_Componente.obtenerIDComponente(Subcomponente_EjecutarTarea_PnlEtapa.Text);
                    string idFase = Administrar_Tareas_Notificaciones.obtenerIDFase(FaseAsig_EjecutarTarea_PnlEtapa.Text);
                    int idEtapa = Administrar_Tareas_Notificaciones.obtenerIDEtapa(idFase, idSubcomponente);
                    // validar el cronograma establecido con el cual se realizar la actividad de la actualizacion de estado
                    // obtener la fecha de finalizacion establecida
                    DateTime fechaFinEstablecida = DateTime.Parse(FechaFin_EjecutarTarea_PnlEtapa.Text);
                    // obtener la fecha de finalizacion actual
                    DateTime fechaFinActual = DateTime.Now;
                    int fechaValida;
                    if (fechaFinActual > fechaFinEstablecida) // se paso de la fecha de finalizacion inicial
                    {
                        fechaValida = 0;
                    }
                    else // cumplio con su cronograma establecido
                    {
                        fechaValida = 1;
                    }
                    Administrar_Tareas_Notificaciones.actualizarEstadoTareaAFinalizada(idEtapa,fechaValida);
                    // actualizar el estado de la tarea y responsable siguiente
                    // mandar el cod de subcomponente para ver si aun sigue en la misma tupla
                    Administrar_Tareas_Notificaciones.validarActualizacionEstadoTareaNext(idEtapa, idSubcomponente);
                    // Enviar el correo en la capa de datos para la proxima persona respomsable de una fase el proceso
                    // obtener datos del usuario para la siguiente notificacion : [iduserNext],[email],[idFaseNext],[codSubcompNext]
                    List<Object> datosPreGet = Administrar_Tareas_Notificaciones.obtenerDatosPreEnviarEmail(idEtapa,idFase);
                    if (!datosPreGet.Equals(null)&&datosPreGet.Count>0) // aun se puede enviar mas correos
                    {
                        string idUserNext=datosPreGet[0].ToString();
                        string idFaseNext=datosPreGet[3].ToString();
                        string codSubcompNext=datosPreGet[2].ToString();
                        string emailNextPersona=datosPreGet[4].ToString();
                        string nombreComp = Gestionar_Componente.obtenerNombreComponenteFromSubcomp(codSubcompNext);
                        List<Object> datosGetEtapa = Administrar_Tareas_Notificaciones.obtenerDatosGeneralesEmail(idUserNext, idFaseNext, codSubcompNext);
                        string asunto = "ALERTA DE NUEVO PROCESO PENDIENTE DE SU REALIZACION";
                        string mensaje = "USTED FUE ASIGNADO PARA LA REALIZACION DE UNA NUEVA FASE DEL PROCESO CON EL SIGUIENTE DETALLE:</br></br></br>" +
                                         "<b>PROYECTO:</b>              " + datosGetEtapa[0].ToString() + "</br></br>" +
                                         "<b>COMPONENTE:</b>            " + nombreComp + "</br></br>" +
                                         "<b>SUBCOMP. O PROCESO:</b>    " + datosGetEtapa[1].ToString() + "</br></br>" +
                                         "<b>FASE:</b>                  " + datosGetEtapa[2].ToString() + "</br></br>" +
                                         "<b>FECHA INICIO:</b>          " + datosGetEtapa[3].ToString() + "</br></br>" +
                                         "<b>FECHA FINALIZACION:</b>    " + datosGetEtapa[4].ToString() + "</br></br></br>" +
                                         "<b>NOTA 1:</b> EN CASO DE QUE LA ANTERIOR FASE NO SE HUBIERA CONCLUIDO CORRECTAMENTE, USTED TIENE LA OPCION DE ACCEDER AL SISTEMA</br>PARA RECHAZAR ESTA NUEVA TAREA O FASE ASIGNADA HASTA QUE EL FLUJO DE LAS FASES SIGUEN SU CUMPLIMIENTO DE LA MANERA CORRECTA</br></br>" +
                                         "<b>NOTA 2:</b> VERIFICAR LA INFORMACION DEL PRESENTE CORREO JUNTO CON LA DEL SISTEMA, GRACIAS....";
                        EnvioEmail servidorCorreo = new EnvioEmail();
                        servidorCorreo.añadirDestinatario(emailNextPersona);
                        servidorCorreo.enviarCorreo(asunto, mensaje);
                    }                    
                    if (fechaValida.Equals(1))
                    {
                        MessageBox.Show("Tarea Finalizada exitosamente dentro del cronograma establecido!!!\nNOTA: Se notificara al responsable de la siguiente Tarea a Realizar, En caso de que la Acepte se Seguira con el Flujo; caso Contrario: Usted Recibira nuevamente notificacion para corregir esta Tarea", "ADMINSTRACION TAREAS Y NOTIFICACIONES");
                    }
                    else
                    {
                        MessageBox.Show("Tarea Finalizada, pero con la observacion de que se excedio el cronograma previamente establecido, tendra un estado de EN MORA, evite esto para futuras tareas asignadas...!!!\nNOTA: Se notificara al responsable de la siguiente Tarea a Realizar, En caso de que la Acepte se Seguira con el Flujo; caso Contrario: Usted Recibira nuevamente notificacion para corregir esta Tarea", "ADMINSTRACION TAREAS Y NOTIFICACIONES");
                    }
                    // verificar si es la ultima fase del subcomponente : [Activacion]
                    if (idFase.Trim().Equals("Fs21"))
                    {
                        MessageBox.Show("El SubComponente fue finalizado con exito, Todas las fases Concluidas!!!");
                        //string codSubcompSelect = Gestionar_Componente.getCodigoComponente(nombreSubcomp);
                        Gestionar_Componente.fnalizarSubcomponente(idSubcomponente);
                    }
                    // finalizar forzosamente la notificacion como vista o leida
                    Administrar_Tareas_Notificaciones.actualizarEstadoNotificacion(Global.idAlerta);
                    // actualizar el valor de la etapa agregando la fecha de finalizacion real
                    Administrar_Tareas_Notificaciones.actualizarFechaFinRealFaseAsignada(idEtapa, DateTime.Today);
                }
                else // tareas de tipo ASIGNACION DE AUTORIZACIONES
                {
                    //string idautoriza    
                    // GENERAR NOTIFICACIONES DINAMICAS CON RESPECTO A LAS FASES Y AUTORIZACIONES
                    string objAsignado=txtAsignadoA_EjecucionTarea_PnlAsignacionAutoriz.Text;
                    string codAutoriz = Gestionar_Autorizacion.obtenerIDAutorizacion(Autorizacion_EjecucionTarea_PnlAsignacionAutoriz.Text) ;
                    DateTime fechaFinEstablecida = DateTime.Parse(FechaPlazo_EjecucionTarea_PnlAsignacionAutoriz.Text);
                    if (txtTipoTarea_EjecucionTarea_AdmTareaFase.Text.Equals("AUTORIZACION DE PROYECTO")) // proyecto autoriz
                    {
                        string codProy = Gestionar_Proyecto.obtenerIDProyecto(objAsignado); // implementar                        
                        int idProyAutoriz = Gestionar_Autorizacion.obtenerIDProyAutoriz(codProy,codAutoriz);
                        // validar las fechas de finalizacion para actualizar estado
                        if (DateTime.Now > fechaFinEstablecida)
                        {
                            Administrar_Tareas_Notificaciones.actualizarEstadoAsignacionAutorizacionFinalizada(idProyAutoriz, 1,false);
                            MessageBox.Show("Tarea de Asignacion de Autorizaciones de Proyecto Finalizada!!!\nNOTA: La Tarea Excedio su fecha de Finalizacion establecida, se registro un estado de EN MORA, tenga en cuenta esto para futuras Tareas asignadas...", "ADMINSTRACION TAREAS Y NOTIFICACIONES");                    
                        }
                        else
                        {
                            Administrar_Tareas_Notificaciones.actualizarEstadoAsignacionAutorizacionFinalizada(idProyAutoriz, 1, true);
                            MessageBox.Show("Tarea de Asignacion de Autorizaciones de Proyecto Finalizada Dentro del Tiempo Establecido..!!!", "ADMINSTRACION TAREAS Y NOTIFICACIONES");                    
                        }
                        // actualizar fecha de finalizacion real de la autorizacion de proyecto
                        Gestionar_Autorizacion.actualizarFechaFinRealAutorizacionAsignada(idProyAutoriz, DateTime.Today, 1);
                    }
                    else // componentes autoriz
                    {
                        string codComp = Gestionar_Componente.obtenerIDComponente(objAsignado);
                        int idCompAutoriz = Gestionar_Autorizacion.obtenerIDCompAutoriz(codComp, codAutoriz);
                        // validar las fechas de finalizacion para actualizar estado
                        if (DateTime.Now > fechaFinEstablecida)
                        {
                            Administrar_Tareas_Notificaciones.actualizarEstadoAsignacionAutorizacionFinalizada(idCompAutoriz, 2,false);
                            MessageBox.Show("Tarea de Asignacion de Autorizaciones de Componente Finalizada!!!\nNOTA: La Tarea Excedio su fecha de Finalizacion establecida, se registro un estado de EN MORA, tenga en cuenta esto para futuras Tareas asignadas...", "ADMINSTRACION TAREAS Y NOTIFICACIONES");                    
                        }
                        else
                        {
                            Administrar_Tareas_Notificaciones.actualizarEstadoAsignacionAutorizacionFinalizada(idCompAutoriz, 2,true);
                            MessageBox.Show("Tarea de Asignacion de Autorizaciones de Componente Finalizada Dentro del Tiempo Establecido..!!!", "ADMINSTRACION TAREAS Y NOTIFICACIONES");                    
                        }
                        // actualizar fecha de finalizacion real de la autorizacion de proyecto
                        Gestionar_Autorizacion.actualizarFechaFinRealAutorizacionAsignada(idCompAutoriz, DateTime.Today, 2);
                    }
                    // finalizar forzosamente la notificacion como vista
                    Administrar_Tareas_Notificaciones.actualizarEstadoNotificacion(Global.idAlerta);
                    
                }
                pnlEjecutarTarea_AdministrarTareaFase.Visible = false;
            }
        }

