        private void gvListadoBoletas_ListadoBoletas_CellDoubleClick(object sender, DataGridViewCellEventArgs e)
        {
            // validar si es que la boleta ya fue devuelta no hacer ninguna de las posibles acciones
            string estadoBoleta = Gestionar_Boleta.obtenerEstadoEntrega(int.Parse(gvListadoBoletas_ListadoBoletas[0, e.RowIndex].Value.ToString()));
            if (!estadoBoleta.Trim().Equals("Devuelta"))
            {
                // generar los eventos de renovar las fechas y tambien la de el estado de devolucion
                if (MetroMessageBox.Show(this, "Desea Renovar el Cronograma de Fechas de la boleta seleccionada??", "LISTADO DE BOLETAS", MessageBoxButtons.YesNo) == DialogResult.Yes)
                {
                    pnlListadoBoletas_RegBoletaSubcomp.Visible = false;
                    pnlRenovacionCronograma_RegistroBoletaSubcomp.Visible = true;
                    // pasar los datos que se obtuvieron de la tabla anterior
                    Global.idboleta = int.Parse(gvListadoBoletas_ListadoBoletas[0, e.RowIndex].Value.ToString());
                    txtProyecto_RenovacionCronogramaBoleta.Text = gvListadoBoletas_ListadoBoletas[3, e.RowIndex].Value.ToString();
                    txtSubcomponente_RenovacionCronogramaBoleta.Text = gvListadoBoletas_ListadoBoletas[2, e.RowIndex].Value.ToString();
                    txtTipoBoleta_RenovacionCronogramaBoleta.Text = gvListadoBoletas_ListadoBoletas[1, e.RowIndex].Value.ToString();
                    txtFechaEmision_RenovacionCronogramaBoleta.Text = gvListadoBoletas_ListadoBoletas[7, e.RowIndex].Value.ToString();
                    txtFechaVencimiento_RenovacionCronogramaBoleta.Text = gvListadoBoletas_ListadoBoletas[8, e.RowIndex].Value.ToString();
                }
                else if (MetroMessageBox.Show(this, "la Boleta Seleccionada ya fue devuelta al Proveedor???", "LISTADO DE BOLETAS", MessageBoxButtons.YesNo) == DialogResult.Yes)
                {
                    int idboleta = int.Parse(gvListadoBoletas_ListadoBoletas[0, e.RowIndex].Value.ToString());
                    Gestionar_Boleta.devolverBoleta(idboleta);
                    MessageBox.Show("El estado de la boleta se guardo correctamente, el reporte posterior no tomara en cuenta esta boleta!!!\nNOTA: Certificar de manera real que la boleta fue entregada a su respectivo proveedor", "DEVOLUCION DE LA BOLETA");
                }
                else
                {
                    return;
                }

            }
            else
            {
                MetroMessageBox.Show(this, "La Boleta ya no esta disponible para realizar alguna accion por que ya fue devuelta", "Estado de Boletas");
            }
            
        }

