        private void btnGenerarReporte_EvaluacionCronogramaProyectos_Click(object sender, EventArgs e)
        {
            ReportDocument reporte = new ReportDocument();
            string direccion = Path.Combine(rutaRun, @"rptEvaluacionCronogramaProyectos.rpt");
            reporte.Load(direccion);

            //OdbcDataAdapter adapter = new OdbcDataAdapter("reporteEvaluacionCronogramaProyectos", conexionActual2);
            OdbcDataAdapter adapter = new OdbcDataAdapter(new OdbcCommand("{?= call reporteEvaluacionCronogramaProyectos (?)}", conexionActual2));
            adapter.SelectCommand.CommandType = CommandType.StoredProcedure;
            
            OdbcParameter param = new OdbcParameter("@RETURN_VALUE", OdbcType.Int);
            param.Direction = ParameterDirection.ReturnValue;
            adapter.SelectCommand.Parameters.Add(param);

            adapter.SelectCommand.Parameters.Add("@tipo", OdbcType.Int);
            adapter.SelectCommand.Parameters[1].Value = cmbTipoProyecto__EvaluacionCronogramaProyectos.SelectedIndex;
            Seguimiento_ProyectosYPFB_AviacionDataSet datataset = new Seguimiento_ProyectosYPFB_AviacionDataSet();
            datataset.EnforceConstraints = false;
            // cambiar el valor de los atributos del informe
            if (adapter.SelectCommand.Parameters[1].Value.Equals(0))
            {
                ((TextObject)reporte.ReportDefinition.ReportObjects["lblTipoProyecto_EvaluacionCronogramaProyectos"]).Text = "Todos";    
            }
            else
            {
                ((TextObject)reporte.ReportDefinition.ReportObjects["lblTipoProyecto_EvaluacionCronogramaProyectos"]).Text = Gestionar_Proyecto.obtenerNombreTipoProyecto(adapter.SelectCommand.Parameters[1].Value);          
            }
            adapter.Fill(datataset, "reporteEvaluacionCronogramaProyectos");
            reporte.SetDataSource(datataset);
            rptReporte8.ReportSource = reporte;
            reporte.Refresh();

        }

