        private void btnGuardar_RegistrarPersonal_Click(object sender, EventArgs e)
        {
            string alias = txtAlias_RegistrarPersonal.Text;
            string nombres = txtNombres_RegistrarPersonal.Text;
            string apellidos = txtApellidos_RegistrarPersonal.Text;
            char sexo;
            if (rbtnSexoMasculino_RegistrarPersonal.Checked)
            {
                sexo = 'M';
            }
            else if (rbtnSexoFemenino_RegistrarPersonal.Checked)
            {
                sexo = 'F';
            }
            else
            {
                MessageBox.Show("Sexo: Campo Obligatorio para Registro del Personal", "REGISTRAR PERSONAL");
                return;
            }
            string email = txtEmail_RegistrarPersonal.Text;
            string idcargo = cmbCargo_RegistrarPersonal.SelectedValue.ToString();
            if (alias.Trim().Length > 0 && email.Length>0 && nombres.Length>0 && apellidos.Length>0)
            {
                if (Gestionar_Personal.registrarPersonal(alias, nombres, apellidos, sexo, email, idcargo))
                {
                    MessageBox.Show("Datos de Personal Registrado Correctamente", "REGISTRAR PERSONAL");
                }
                else
                {
                    MessageBox.Show("Registro de personal no se llevo con exito, vuelva a intentarlo porfavor", "REGISTRAR PERSONAL");
                }
                btnGuardar_RegistrarPersonal.Enabled = false;
                btnCancelar_RegistrarPersonal.Enabled = false;
                btnNuevo_RegistrarPersonal.Enabled = true;
            }
            else
            {
                MessageBox.Show("Rellenar todos los campos porfavor","REGISTRO DE PERSONAL");
            }
            
        }

