        private void gvListadoPersonal_CellDoubleClick(object sender, DataGridViewCellEventArgs e)
        {
            if (e.RowIndex >= 0) // validar que sea un campo de los registros
            {
                if (MetroMessageBox.Show(this, "Desea Actualizar Datos de la Persona Seleccionada", "LISTADO PERSONAL", MessageBoxButtons.YesNo) == DialogResult.Yes)
                {
                    // pasar los valores necesarios al siguiente panel que es el de actualizacion de datos del personal
                    pnlListadoPersonal_GestionarPersonal.Visible = false;
                    pnlActualizarPersonal_GestionarPersonal.Visible = true;
                    cargarCmbArea_ActualizarPersonal();
                    //cargarCmbCargo_ActualizarPersonal();
                    txtAlias_ActualizarPersonal.Text = gvListadoPersonal[0, e.RowIndex].Value.ToString();
                    txtNombres_ActualizarPersonal.Text = gvListadoPersonal[1, e.RowIndex].Value.ToString();
                    txtApellidos_ActualizarPersonal.Text = gvListadoPersonal[2, e.RowIndex].Value.ToString();
                    txtEmail_ActualizarPersonal.Text = gvListadoPersonal[3, e.RowIndex].Value.ToString();
                    txtCargo_ActualizarPersonal.Text = gvListadoPersonal[4, e.RowIndex].Value.ToString();
                    txtArea_ActualizarPersonal.Text = gvListadoPersonal[5, e.RowIndex].Value.ToString();

                }
                else if (MetroMessageBox.Show(this,"Desea Eliminar el Registro de la Persona Seleccionada?", "LISTADO PERSONAL", MessageBoxButtons.YesNo) == DialogResult.Yes)
                {
                    string aliasSelect=gvListadoPersonal[0,e.RowIndex].Value.ToString();
                    if (Gestionar_Personal.eliminarPersonal(aliasSelect))
                    {
                        MessageBox.Show("Registro Eliminado Exitosamente","LISTADO PERSONAL");
                        gvListadoPersonal.DataSource = Gestionar_Personal.showPersonal();
                        Auxiliar.ajustarTamañoTabla(gvListadoPersonal);
                    }
                }
                else
                {
                    return;
                }
            }
        }

